
var  APP = APP || {};

var ready = (callback) => {
      if (document.readyState != "loading") callback();
      else document.addEventListener("DOMContentLoaded", callback);
}

var root = document.querySelectorAll('html, body');

// Start Basic Functions

function _GET(param) {
      var sPageURL = window.location.search.substring(1),
            sURLVariables = sPageURL.split('&'),
            parameterName,
            i;

      for (i = 0; i < sURLVariables.length; i++) {
            parameterName = sURLVariables[i].split('=');

            if (parameterName[0] === param) {
                  return parameterName[1] === undefined ? true : decodeURIComponent(parameterName[1]);
            }
      }
};

function scrollIt(destination, duration = 200, easing = 'linear', callback) {

      const easings = {
            linear(t) {
                  return t;
            },
            easeInQuad(t) {
                  return t * t;
            },
            easeOutQuad(t) {
                  return t * (2 - t);
            },
            easeInOutQuad(t) {
                  return t < 0.5 ? 2 * t * t : -1 + (4 - 2 * t) * t;
            },
            easeInCubic(t) {
                  return t * t * t;
            },
            easeOutCubic(t) {
                  return (--t) * t * t + 1;
            },
            easeInOutCubic(t) {
                  return t < 0.5 ? 4 * t * t * t : (t - 1) * (2 * t - 2) * (2 * t - 2) + 1;
            },
            easeInQuart(t) {
                  return t * t * t * t;
            },
            easeOutQuart(t) {
                  return 1 - (--t) * t * t * t;
            },
            easeInOutQuart(t) {
                  return t < 0.5 ? 8 * t * t * t * t : 1 - 8 * (--t) * t * t * t;
            },
            easeInQuint(t) {
                  return t * t * t * t * t;
            },
            easeOutQuint(t) {
                  return 1 + (--t) * t * t * t * t;
            },
            easeInOutQuint(t) {
                  return t < 0.5 ? 16 * t * t * t * t * t : 1 + 16 * (--t) * t * t * t * t;
            }
      };

      const start = window.pageYOffset;
      const startTime = 'now' in window.performance ? performance.now() : new Date().getTime();

      const documentHeight = Math.max(document.body.scrollHeight, document.body.offsetHeight, document.documentElement.clientHeight, document.documentElement.scrollHeight, document.documentElement.offsetHeight);
      const windowHeight = window.innerHeight || document.documentElement.clientHeight || document.getElementsByTagName('body')[0].clientHeight;
      const destinationOffset = typeof destination === 'number' ? destination : destination.offsetTop;
      const destinationOffsetToScroll = Math.round(documentHeight - destinationOffset < windowHeight ? documentHeight - windowHeight : destinationOffset);

      if ('requestAnimationFrame' in window === false) {
            window.scroll(0, destinationOffsetToScroll);
            if (callback) {
                  callback();
            }
            return;
      }

      function scroll() {
            const now = 'now' in window.performance ? performance.now() : new Date().getTime();
            const time = Math.min(1, ((now - startTime) / duration));
            const timeFunction = easings[easing](time);
            window.scroll(0, Math.ceil((timeFunction * (destinationOffsetToScroll - start)) + start));

            if (window.pageYOffset === destinationOffsetToScroll) {
                  if (callback) {
                        callback();
                  }
                  return;
            }

            requestAnimationFrame(scroll);
      }

      scroll();
}


// End Basic Functions

APP.script = {
      // initialize script.min.js
      init: function () {
            this.touchDevice;
            this.userAgent;
            this.windowDimensions;
            this.search;

            this.touchDevice = false;
            if (navigator.userAgent.match(/Android|webOS|iPhone|iPad|iPod|BlackBerry|IEMobile|Opera Mini/)) {
                  this.touchDevice = true;
            }
      }
}

ready(() => {
      APP.script.init();
});

var eventSelector = document.querySelectorAll("a[data-target-section]");
for (var i = 0; i < eventSelector.length; i++) {
      eventSelector[i].addEventListener("click", (e) => {
            e.preventDefault();
            var targetSection = e.target.dataset.targetSection;
            var section = document.querySelector('section[data-name="' + targetSection + '"]').offsetTop;
            var offset = section + document.querySelector('#main > article').offsetTop;

            if (section != 0)
                  offset = offset - 115;

            scrollIt(
                  offset,
                  300,
                  'easeOutQuad'
            );
      });
}
